mapper.select:
select a.name,u.name as owner,a.startDate,a.endDate from tbl_activity a JOIN tbl_user u on a.owner = u.id
a.name like '%' #{name[0]} '%'
and u.name like '%' #{owner[0]} '%'
and a.startDate > #{startDate[0]}
and a.endDate < #{endDate[0]}
order by a.createTime desc

select count(*) from tbl_activity a join tbl_user u
on a.owner = u.id



        a.name like '%' #{name[0]} '%'


        and u.name like '%' #{owner[0]} '%'


        and a.startDate > #{startDate[0]}


        and a.endDate < #{endDate[0]}
